this.setState({
  searchQuery:searchQuery
})
 }
 
 render(){
console.log(this.state.results)

  return (
    <div className="App">

    <div className="search-bar">
   
   <input className="search-placeholder" type="text" onChange={(e)=>{this.renderSearch(e)}}/>
  
  <button onClick={this.getResults} style={{backgroundColor:"#000"}}><img src="./Assets/search.png" width="5" height="6"/></button>
  {/*  <input type="submit"  value="Search" onClick={this.getResults}/> */}

    </div>


   <p>Search results for the term {this.state.searchQuery}</p>

  {this.state.results.map((x)=>{
console.log("x  koji:  "+ JSON.stringify(x))
 return  <img src={x.links[0].href} style={{width:"30%",height:"300px"}} />
 
 



})}
       </div>
  );

 }
 
 
}

export default SearchPlatform;
